// Project euler problem 8
//  - find the largest product of 13 adjacent digits in given number

#include <stdio.h>
#include <gmp.h>

int n,m;
mpz_t current_product, largest_product;

unsigned char number[] = "";

int main(){
	mpz_init(largest_product);
	mpz_init(current_product);
	
	printf("\nProject Euler Problem 8:\n- find the largest product of 13 adjacent digits in given number\n\n");
	// turn string into numbers
	for(n = 0; n < 1000; n++){
		number[n] -= 48;
		//printf("%d", number[n]);
	}
	
	// find the first product
	for(n = 0; n < (1000 - 13); n++){
		if(number[n] != 0){
			mpz_set_ui(current_product, (unsigned long)number[n]);
			for(m = 1; m < 13; m++){
				// product *= number[m+n]
				mpz_mul_ui(current_product, current_product, (unsigned long) number[n+m]);
			}
			// compare current product to largest one
			if(mpz_cmp(current_product, largest_product) > 0){
				mpz_swap(current_product, largest_product);
			}
			mpz_set_ui(current_product, 0);
		}
	}
	
	printf("Largest Product = ");
	mpz_out_str(stdout, 10, largest_product);
	printf("\n\n");
}
